92D;  \r\n      CalendarData[8]=0x5192B;  \r\n      CalendarData[9]=0xA95;  \r\n      CalendarData[10]=0xB4A;  \r\n      CalendarData[11]=0x416AA;  \r\n      CalendarData[12]=0xAD5;  \r\n      CalendarData[13]=0x90AB5;  \r\n      CalendarData[14]=0x4BA;  \r\n      CalendarData[15]=0xA5B;  \r\n      CalendarData[16]=0x60A57;  \r\n      CalendarData[17]=0x52B;  \r\n      CalendarData[18]=0xA93;  \r\n      CalendarData[19]=0x40E95;  \r\n      madd[0]=0;  \r\n      madd[1]=31;  \r\n      madd[2]=59;  \r\n      madd[3]=90;  \r\n      madd[4]=120;  \r\n      madd[5]=151;  \r\n      madd[6]=181;  \r\n      madd[7]=212;  \r\n      madd[8]=243;  \r\n      madd[9]=273;  \r\n      madd[10]=304;  \r\n      madd[11]=334;  \r\n    }  \r\n   \r\n  function   GetBit(m,n)  \r\n  {    \r\n        return   (m>>n)&1;  \r\n  }  \r\n   \r\n  function   e2c()  \r\n  {      \r\n      var   total,m,n,k;  \r\n      var   isEnd=false;  \r\n      var   tmp=TheDate.getYear();  \r\n      if   (tmp<1900)     tmp+=1900;  \r\n      total=(tmp-2001)*365  \r\n          +Math.floor((tmp-2001)/4)  \r\n          +madd[TheDate.getMonth()]  \r\n          +TheDate.getDate()  \r\n          -23;  \r\n      if   (TheDate.getYear()%4==0&&TheDate.getMonth()>1)  \r\n          total++;  \r\n      for(m=0;;m++)  \r\n      {      \r\n          k=(CalendarData[m]<0xfff)?11:12;  \r\n          for(n=k;n>=0;n--)  \r\n          {  \r\n              if(total<=29+GetBit(CalendarData[m],n))  \r\n              {    \r\n                  isEnd=true;  \r\n                  break;  \r\n              }  \r\n              total=total-29-GetBit(CalendarData[m],n);  \r\n          }  \r\n          if(isEnd)break;  \r\n      }  \r\n      cYear=2001   +   m;  \r\n      cMonth=k-n+1;  \r\n      cDay=total;  \r\n      if(k==12)  \r\n      {  \r\n          if(cMonth==Math.floor(CalendarData[m]/0x10000)+1)  \r\n              cMonth=1-cMonth;  \r\n          if(cMonth>Math.floor(CalendarData[m]/0x10000)+1)  \r\n              cMonth--;  \r\n      }  \r\n      cHour=Math.floor((TheDate.getHours()+3)/2);  \r\n  }  \r\n   \r\n  function   GetcDateString()  \r\n  {   var   tmp=\"\";  \r\n      tmp+=tgString.charAt((cYear-4)%10);       //年干  \r\n      tmp+=dzString.charAt((cYear-4)%12);       //年支  \r\n      tmp+=\"年(\";  \r\n      tmp+=sx.charAt((cYear-4)%12);  \r\n      tmp+=\")   \";  \r\n      if(cMonth<1)  \r\n      {    \r\n        tmp+=\"闰\";  \r\n          tmp+=monString.charAt(-cMonth-1);  \r\n      }  \r\n      else  \r\n          tmp+=monString.charAt(cMonth-1);  \r\n      tmp+=\"月\";  \r\n      tmp+=(cDay<11)?\"初\":((cDay<20)?\"十\":((cDay<30)?\"廿\":\"卅\"));  \r\n      if(cDay%10!=0||cDay==10)  \r\n          tmp+=numString.charAt((cDay-1)%10);  \r\n      tmp+=\"    \";  \r\n      if(cHour==13)tmp+=\"夜\";  \r\n          tmp+=dzString.charAt((cHour-1)%12);  \r\n      tmp+=\"时\";  \r\n      cDateString=tmp;  \r\n      return   tmp;  \r\n  }  \r\n   \r\n  function   GetDateString()  \r\n  {    \r\n      var   tmp=\"\";  \r\n      var   t1=TheDate.getYear();  \r\n      if   (t1<1900)t1+=1900;  \r\n      tmp+=t1  \r\n                +\"年\"  \r\n                +(TheDate.getMonth()+1)+\"月\"  \r\n                +TheDate.getDate()+\"日   \"  \r\n                +TheDate.getHours()+\":\"  \r\n                +((TheDate.getMinutes()<10)?\"0\":\"\")  \r\n                +TheDate.getMinutes()\r\n                +\"   星期\"+weekString.charAt(TheDate.getDay());    \r\n      DateString=tmp;  \r\n      return   tmp;  \r\n  }  \r\n   \r\n  init();  \r\n  e2c();  \r\n  GetDateString();  \r\n  GetcDateString();  \r\n  document.write(DateString,\"农历\",cDateString);  \r\n</script>\r\n\r\n</td>\r\n\r\n<td width=\"580\">\r\n<form name=search_js1 method=post action=''[!--news.url--]e/search/index.php'' onsubmit=''return search_check(document.search_js1);''>\r\n<div align=\"right\">\r\n<select name=show><option value=title>\r\n标题</option><option value=smalltext>\r\n简介</option><option value=newstext>\r\n内容</option><option 